import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { ChevronLeft, ChevronRight, Briefcase, Brain, Users, Target } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Link } from "wouter";

const categories = [
  {
    id: 1,
    name: "Business Analysis",
    description: "CBAP, ECBA, CCBA Certifications",
    icon: Briefcase,
    href: "/courses/cbap",
    gradient: "from-primary to-primary/80",
  },
  {
    id: 2,
    name: "Gen AI Courses",
    description: "AI-Powered Professional Skills",
    icon: Brain,
    href: "/courses/genai-scrum-master",
    gradient: "from-secondary to-secondary/80",
  },
  {
    id: 3,
    name: "Agile & Scrum",
    description: "Scrum Master, Product Owner",
    icon: Users,
    href: "/courses",
    gradient: "from-purple-600 to-purple-500",
  },
  {
    id: 4,
    name: "Project Management",
    description: "PMP Certification Training",
    icon: Target,
    href: "/courses/pmp",
    gradient: "from-primary to-secondary",
  },
];

export default function CategoriesCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % categories.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const goToNext = () => {
    setCurrentIndex((prev) => (prev + 1) % categories.length);
  };

  const goToPrev = () => {
    setCurrentIndex((prev) => (prev - 1 + categories.length) % categories.length);
  };

  return (
    <section className="py-20 md:py-24 bg-gray-50" data-testid="section-categories">
      <div className="max-w-7xl mx-auto px-6">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold mb-4 text-gray-900" data-testid="text-categories-title">
            Favorite Topics to Learn
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Explore our most popular certification categories and advance your career
          </p>
        </motion.div>

        <div className="relative">
          <div className="hidden lg:grid lg:grid-cols-4 gap-6">
            {categories.map((category, index) => (
              <CategoryCard key={category.id} category={category} index={index} />
            ))}
          </div>

          <div className="lg:hidden relative h-80 overflow-hidden">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentIndex}
                initial={{ opacity: 0, x: 100 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -100 }}
                transition={{ duration: 0.5 }}
                className="absolute inset-0"
              >
                <CategoryCard category={categories[currentIndex]} index={currentIndex} />
              </motion.div>
            </AnimatePresence>

            <Button
              size="icon"
              className="absolute left-4 top-1/2 -translate-y-1/2 z-10 bg-primary hover:bg-primary/90 text-white shadow-lg"
              onClick={goToPrev}
              data-testid="button-carousel-prev"
            >
              <ChevronLeft />
            </Button>

            <Button
              size="icon"
              className="absolute right-4 top-1/2 -translate-y-1/2 z-10 bg-primary hover:bg-primary/90 text-white shadow-lg"
              onClick={goToNext}
              data-testid="button-carousel-next"
            >
              <ChevronRight />
            </Button>
          </div>

          <div className="flex justify-center gap-2 mt-6 lg:hidden">
            {categories.map((_, index) => (
              <button
                key={index}
                className={`h-2 rounded-full transition-all duration-300 ${
                  index === currentIndex ? "bg-primary w-8" : "bg-gray-300 w-2"
                }`}
                onClick={() => setCurrentIndex(index)}
                data-testid={`button-carousel-dot-${index}`}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

function CategoryCard({ category, index }: { category: typeof categories[0]; index: number }) {
  const Icon = category.icon;

  return (
    <Link href={category.href}>
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 0.6, delay: index * 0.1 }}
        className={`group relative h-72 rounded-xl overflow-hidden cursor-pointer shadow-[0_4px_16px_rgba(0,0,0,0.08)] hover:shadow-[0_8px_32px_rgba(0,0,0,0.15)] transition-all duration-300`}
        data-testid={`card-category-${category.id}`}
      >
        <div className={`absolute inset-0 bg-gradient-to-br ${category.gradient}`} />

        <div className="absolute inset-0 opacity-10">
          <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNjAiIGhlaWdodD0iNjAiIHZpZXdCb3g9IjAgMCA2MCA2MCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj48ZyBmaWxsPSJub25lIiBmaWxsLXJ1bGU9ImV2ZW5vZGQiPjxnIGZpbGw9IiNmZmZmZmYiIGZpbGwtb3BhY2l0eT0iMC4zIj48Y2lyY2xlIGN4PSIzMCIgY3k9IjMwIiByPSI0Ii8+PC9nPjwvZz48L3N2Zz4=')]"></div>
        </div>

        <div className="relative h-full p-6 flex flex-col justify-between text-white">
          <div className="w-14 h-14 rounded-xl bg-white/20 backdrop-blur-sm flex items-center justify-center transition-transform duration-300 group-hover:scale-110 shadow-lg">
            <Icon className="w-7 h-7" />
          </div>

          <div>
            <h3 className="text-2xl font-bold mb-2">{category.name}</h3>
            <p className="text-white/80 text-sm">{category.description}</p>
          </div>
        </div>

        <div className="absolute bottom-0 left-0 right-0 h-1 bg-white/30 transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300 origin-left"></div>
      </motion.div>
    </Link>
  );
}

