client/src/components/Header.tsx.content:
// Updated: Uses CurrencyContext for country/city selection with robust null-safe implementation
import { useState, useEffect, useRef } from "react";
import { Menu, X, ChevronDown, ChevronRight, Globe, MapPin } from "lucide-react";
import { Button } from "@/components/ui/button";
import { motion, AnimatePresence } from "framer-motion";
import { Link, useLocation } from "wouter";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useCurrency } from "@/lib/currencyContext";
import { getCitiesForCountry, getAvailableCountries, getCurrencyCode } from "@/lib/currency";

interface SubMenuItem {
  name: string;
  href: string;
}

interface CourseCategory {
  name: string;
  href?: string;
  subItems?: SubMenuItem[];
}

const courseCategories: CourseCategory[] = [
  { 
    name: "All Courses", 
    href: "/courses" 
  },
  { 
    name: "Project Management", 
    href: "/courses" 
  },
  { 
    name: "Business Management", 
    subItems: [
      { name: "CBAP", href: "/courses/cbap" },
      { name: "ECBA", href: "/courses/ecba" },
      { name: "CCBA", href: "/courses/ccba" },
      { name: "CCBA Prep Course", href: "/courses/ccba-prep" },
    ]
  },
  { 
    name: "Agile and Scrum", 
    href: "/courses" 
  },
  { 
    name: "Gen AI Courses", 
    subItems: [
      { name: "Scrum Master", href: "/courses/genai-scrum-master" },
      { name: "Project Managers", href: "/courses/genai-project-managers" },
      { name: "Product Owner", href: "/courses/genai-product-owner" },
      { name: "Business Analysts", href: "/courses/genai-business-analysts" },
      { name: "Interview Prep for Scrum Masters", href: "/courses/genai-interview-prep" },
    ]
  },
  { 
    name: "Quality Management", 
    href: "/courses" 
  },
  { 
    name: "IT Service Management", 
    href: "/courses" 
  },
  { 
    name: "DevOps", 
    href: "/courses" 
  },
  { 
    name: "Skills Training", 
    href: "/courses" 
  },
  { 
    name: "Cyber Security", 
    href: "/courses" 
  },
];

export default function Header() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isCoursesOpen, setIsCoursesOpen] = useState(false);
  const [activeSubmenu, setActiveSubmenu] = useState<string | null>(null);
  const [isCountryModalOpen, setIsCountryModalOpen] = useState(false);
  const [mobileExpandedCategories, setMobileExpandedCategories] = useState<string[]>([]);
  const [, setLocation] = useLocation();

  // Use CurrencyContext for country/city state (shared across app)
  const { country: selectedCountry, city: selectedCity, setCountry, setCity, currencyCode } = useCurrency();

  // Get available countries and cities (null-safe)
  const availableCountries = getAvailableCountries();
  const availableCities = selectedCountry ? getCitiesForCountry(selectedCountry) : [];

  const coursesRef = useRef<HTMLDivElement>(null);
  const countryRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (coursesRef.current && !coursesRef.current.contains(event.target as Node)) {
        setIsCoursesOpen(false);
        setActiveSubmenu(null);
      }
      if (countryRef.current && !countryRef.current.contains(event.target as Node)) {
        setIsCountryModalOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Submit handler outputs selection object with country, city, and currency
  const handleCountrySubmit = () => {
    if (selectedCountry && selectedCity) {
      const selection = {
        country: selectedCountry,
        city: selectedCity,
        currency: currencyCode,
      };
      console.log("Location selected:", selection);
      alert(`Selected Location:\nCountry: ${selectedCountry}\nCity: ${selectedCity}\nCurrency: ${currencyCode}`);
      setIsCountryModalOpen(false);
    } else {
      alert("Please select both country and city");
    }
  };

  // Safe country change handler - resets city when country changes
  const handleCountryChange = (value: string) => {
    setCountry(value);
  };

  // Safe city change handler
  const handleCityChange = (value: string) => {
    setCity(value);
  };

  const toggleMobileCategory = (categoryName: string) => {
    setMobileExpandedCategories(prev => 
      prev.includes(categoryName) 
        ? prev.filter(c => c !== categoryName)
        : [...prev, categoryName]
    );
  };

  const handleCourseClick = (href: string) => {
    setIsCoursesOpen(false);
    setActiveSubmenu(null);
    setIsMobileMenuOpen(false);
    setLocation(href);
  };

  return (
    <header
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
        isScrolled
          ? "bg-white/95 backdrop-blur-md shadow-lg"
          : "bg-white/90 backdrop-blur-sm"
      }`}
      data-testid="header"
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6">
        <div className="flex items-center justify-between h-16 sm:h-20">
          <Link href="/">
            <span
              className="text-xl sm:text-2xl font-bold text-primary cursor-pointer tracking-tight"
              data-testid="link-logo"
            >
              AceSynergi
            </span>
          </Link>

          <nav className="hidden lg:flex items-center gap-6">
            <div 
              ref={coursesRef}
              className="relative"
              onMouseEnter={() => setIsCoursesOpen(true)}
              onMouseLeave={() => {
                setIsCoursesOpen(false);
                setActiveSubmenu(null);
              }}
            >
              <button
                className="flex items-center gap-1 text-foreground hover:text-primary transition-colors font-medium py-2"
                onClick={() => setIsCoursesOpen(!isCoursesOpen)}
                data-testid="dropdown-courses"
              >
                Course
                <ChevronDown className={`w-4 h-4 transition-transform ${isCoursesOpen ? 'rotate-180' : ''}`} />
              </button>

              <AnimatePresence>
                {isCoursesOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    transition={{ duration: 0.2 }}
                    className="absolute top-full left-0 mt-1 w-64 bg-white rounded-lg shadow-xl border border-gray-100 z-50"
                  >
                    {courseCategories.map((category, index) => (
                      <div
                        key={category.name}
                        className="relative"
                        onMouseEnter={() => category.subItems && setActiveSubmenu(category.name)}
                        onMouseLeave={() => setActiveSubmenu(null)}
                      >
                        {category.subItems ? (
                          <button
                            onClick={() => setActiveSubmenu(activeSubmenu === category.name ? null : category.name)}
                            onKeyDown={(e) => {
                              if (e.key === 'Enter' || e.key === ' ') {
                                e.preventDefault();
                                setActiveSubmenu(activeSubmenu === category.name ? null : category.name);
                              } else if (e.key === 'ArrowRight' && category.subItems) {
                                setActiveSubmenu(category.name);
                              } else if (e.key === 'Escape') {
                                setActiveSubmenu(null);
                              }
                            }}
                            aria-haspopup="true"
                            aria-expanded={activeSubmenu === category.name}
                            className={`w-full flex items-center justify-between px-4 py-3 text-sm transition-colors ${
                              index === 0 ? 'rounded-t-lg' : ''
                            } ${
                              index === courseCategories.length - 1 ? 'rounded-b-lg' : ''
                            } ${
                              activeSubmenu === category.name 
                                ? 'bg-primary/10 text-primary' 
                                : 'text-gray-700 hover:bg-gray-50'
                            }`}
                            data-testid={`dropdown-category-${category.name.toLowerCase().replace(/\s+/g, '-')}`}
                          >
                            <span className="font-medium">{category.name}</span>
                            <ChevronRight className="w-4 h-4" />
                          </button>
                        ) : (
                          <button
                            onClick={() => handleCourseClick(category.href!)}
                            className={`w-full text-left px-4 py-3 text-sm text-gray-700 hover:bg-gray-50 hover:text-primary transition-colors font-medium ${
                              index === 0 ? 'rounded-t-lg' : ''
                            } ${
                              index === courseCategories.length - 1 ? 'rounded-b-lg' : ''
                            }`}
                            data-testid={`dropdown-category-${category.name.toLowerCase().replace(/\s+/g, '-')}`}
                          >
                            {category.name}
                          </button>
                        )}

                        <AnimatePresence>
                          {activeSubmenu === category.name && category.subItems && (
                            <motion.div
                              initial={{ opacity: 0, x: -10 }}
                              animate={{ opacity: 1, x: 0 }}
                              exit={{ opacity: 0, x: -10 }}
                              transition={{ duration: 0.15 }}
                              className="absolute left-full top-0 w-64 bg-white rounded-lg shadow-xl border border-gray-100 ml-1 z-[60]"
                              onMouseEnter={() => setActiveSubmenu(category.name)}
                              onMouseLeave={() => setActiveSubmenu(null)}
                              role="menu"
                              aria-label={`${category.name} submenu`}
                            >
                              {category.subItems.map((subItem, index) => (
                                <button
                                  key={subItem.name}
                                  onClick={() => handleCourseClick(subItem.href)}
                                  onKeyDown={(e) => {
                                    if (e.key === 'Escape' || e.key === 'ArrowLeft') {
                                      setActiveSubmenu(null);
                                    }
                                  }}
                                  role="menuitem"
                                  tabIndex={0}
                                  className="w-full text-left px-4 py-3 text-sm text-gray-700 hover:bg-primary/10 hover:text-primary transition-colors first:rounded-t-lg last:rounded-b-lg focus:bg-primary/10 focus:text-primary focus:outline-none"
                                  data-testid={`submenu-item-${subItem.name.toLowerCase().replace(/\s+/g, '-')}`}
                                >
                                  {subItem.name}
                                </button>
                              ))}
                            </motion.div>
                          )}
                        </AnimatePresence>
                      </div>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            <Link href="/corporate">
              <span className="text-foreground hover:text-primary transition-colors font-medium cursor-pointer">
                Corporate
              </span>
            </Link>

            <Link href="/contact">
              <span className="text-foreground hover:text-primary transition-colors font-medium cursor-pointer">
                Contact Us
              </span>
            </Link>

            <div ref={countryRef} className="relative">
              <button
                onClick={() => setIsCountryModalOpen(!isCountryModalOpen)}
                className="flex items-center gap-1.5 text-foreground hover:text-primary transition-colors font-medium py-2"
                data-testid="dropdown-country"
              >
                <Globe className="w-4 h-4" />
                Country
                <ChevronDown className={`w-4 h-4 transition-transform ${isCountryModalOpen ? 'rotate-180' : ''}`} />
              </button>

              <AnimatePresence>
                {isCountryModalOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: 10, scale: 0.95 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, y: 10, scale: 0.95 }}
