@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700;800&family=Inter:wght@300;400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 220 18% 20%;

    --card: 0 0% 100%;
    --card-foreground: 220 18% 20%;

    --popover: 0 0% 100%;
    --popover-foreground: 220 18% 20%;

    --primary: 220 75% 40%;
    --primary-foreground: 0 0% 100%;

    --secondary: 188 94% 43%;
    --secondary-foreground: 0 0% 100%;

    --muted: 210 40% 96%;
    --muted-foreground: 220 13% 46%;

    --accent: 38 92% 50%;
    --accent-foreground: 0 0% 100%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 220 13% 91%;
    --input: 220 13% 91%;
    --ring: 220 75% 40%;

    --radius: 0.75rem;

    --chart-1: 220 75% 40%;
    --chart-2: 188 94% 43%;
    --chart-3: 188 85% 55%;
    --chart-4: 38 92% 50%;
    --chart-5: 38 90% 70%;
  }

  .dark {
    --background: 220 75% 12%;
    --foreground: 0 0% 100%;

    --card: 220 65% 15%;
    --card-foreground: 0 0% 100%;

    --popover: 220 65% 15%;
    --popover-foreground: 0 0% 100%;

    --primary: 220 75% 50%;
    --primary-foreground: 0 0% 100%;

    --secondary: 188 94% 50%;
    --secondary-foreground: 0 0% 100%;

    --muted: 220 50% 20%;
    --muted-foreground: 220 10% 65%;

    --accent: 38 92% 55%;
    --accent-foreground: 0 0% 100%;

    --destructive: 0 63% 31%;
    --destructive-foreground: 0 0% 100%;

    --border: 220 35% 25%;
    --input: 220 35% 25%;
    --ring: 188 94% 50%;

    --chart-1: 220 75% 50%;
    --chart-2: 188 94% 50%;
    --chart-3: 188 85% 60%;
    --chart-4: 38 92% 55%;
    --chart-5: 38 90% 75%;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  }
  h1, h2, h3, h4, h5, h6 {
    font-family: 'Poppins', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  }
}

@layer utilities {
  .hover-elevate {
    @apply relative transition-all duration-200;
  }

  .hover-elevate::before {
    @apply content-[''] absolute inset-0 rounded-[inherit] pointer-events-none opacity-0 transition-opacity duration-200;
    background: linear-gradient(to bottom, 
      hsl(var(--foreground) / 0.08), 
      hsl(var(--foreground) / 0.04)
    );
  }

  .hover-elevate:hover::before {
    @apply opacity-100;
  }

  .active-elevate-2 {
    @apply relative transition-all duration-150;
  }

  .active-elevate-2::after {
    @apply content-[''] absolute inset-0 rounded-[inherit] pointer-events-none opacity-0 transition-opacity duration-150;
    background: linear-gradient(to bottom, 
      hsl(var(--foreground) / 0.12), 
      hsl(var(--foreground) / 0.06)
    );
  }

  .active-elevate-2:active::after {
    @apply opacity-100;
  }

  .no-default-hover-elevate:hover::before {
    @apply opacity-0 !important;
  }

  .no-default-active-elevate:active::after {
    @apply opacity-0 !important;
  }

  .toggle-elevate {
    @apply relative transition-all duration-200;
  }

  .toggle-elevate::before {
    @apply content-[''] absolute inset-0 rounded-[inherit] pointer-events-none opacity-0 transition-opacity duration-200;
    background: linear-gradient(to bottom, 
      hsl(var(--foreground) / 0.08), 
      hsl(var(--foreground) / 0.04)
    );
  }

  .toggle-elevated::before {
    @apply opacity-100;
  }
}

